import React, { useState, useEffect } from 'react';  // Correct import of hooks

const ApiService = () => {
  const [businesses, setBusinesses] = useState(null);  // Correct usage of useState
  const [isLoading, setIsLoading] = useState(true);  // Another valid use of useState

  useEffect(() => {  // Correct usage of useEffect
    const fetchData = async () => {
      try {
        const response = await fetch('http://localhost:5000/search');
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }
        const data = await response.json();
        setBusinesses(data);  // Correct update of state with fetched data
        setIsLoading(false);  // Indicate that data fetching is complete
      } catch (error) {
        console.error('Error fetching data: ', error);
      }
    };

    fetchData();  // Correct invocation of the data-fetching function
  }, []);  // Dependency array ensures the effect runs once when the component mounts

  return (
    <div>
      {isLoading ? 'Loading...' : JSON.stringify(businesses)}  // Display loading or data
    </div>
  );
};

export default ApiService;  // Ensure default export for functional component
