import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';  // Import useNavigate
import NavBar from './navbar';
import './homepage.css';

function HomePage() {
  const [input, setInput] = useState('');
  const [events, setEvents] = useState([]);  // State to store fetched events
  const navigate = useNavigate();  // Initialize useNavigate hook

  const handleInputChange = (e) => {
    setInput(e.target.value);
  };

  const fetchEvents = async () => {
    try {
      const response = await fetch('http://localhost:5000/', {
        method: 'GET',  // Fetch events on GET request to the root endpoint
        headers: {
          'Content-Type': 'application/json',
        },
      });
      const data = await response.json();
      setEvents(data.businesses);  // Store events in state
    } catch (error) {
      console.error("Error fetching events:", error);
    }
  };

  const handleSearch = async () => {
    await fetchEvents();
    // After fetching events, navigate to "Events" page with events data
    navigate('/events', { state: { events } });
  };

  return (
    <div className="homepage-container">
      <NavBar />
      <div>
        <h1>Enjoy Vacation, Rain or Shine</h1>
      </div>

      <div className="search-container">
        <input
          type="text"
          placeholder="Enter a city or ZIP code"
          value={input}
          onChange={handleInputChange}
        />
        <button onClick={handleSearch}>Search</button>  // Fetch events and navigate to "Events" page
      </div>
    </div>
  );
}

export default HomePage;
